package com.Bit_Operation;

public class Divide_Substract_Big_Binary {

    public static void main(String[] args) {
        String inputString = "";
        char[] input = inputString.toCharArray();
        int len = input.length;
        int count = 0;
        while (!isZero(input)){
            if(input[len-1] == '1'){
                count++;
                input[len-1] = 0;
            }else{
                makeEven(input);
                count++;
            }
        }
        System.out.println(count);
    }

    static void makeEven(char [] input){
        for (int i=input.length-1; i > 0; i --){
            input[i] = input[i-1];
        }
        input[0] = '0';
    }

    static boolean isZero(char [] input){
        boolean isZero = true;
        for(int i=0; i< input.length; i++){
            if(input[i] == '1'){
                return false;
            }
        }
        return isZero;
    }
}
